mapper.insert:
INSERT INTO t_feed
SET iuser = #{iuser}
, contents = #{contents}
, location = #{location}

mapper.select:
SELECT A.ifeed, A.contents, A.location, A.created_at AS createdAt , B.iuser AS writerIuser, B.nm AS writerNm, B.pic AS writerPic , IF(C.ifeed IS NULL, 0, 1) AS isFav FROM t_feed A INNER JOIN t_user B ON A.iuser = B.iuser
inner join t_feed_fav c
left join t_feed_fav c
on a.ifeed = c.ifeed and c.iuser = #{loginedIuser}
where a.iuser = #{targetIuser}
order by a.ifeed desc limit #{startIdx}, #{rowCount}
